import React, { useState } from 'react';
import { 
    Page,
    Navbar,
    Link,
    List,
    ListItem,
    SwipeoutActions,
    SwipeoutButton,
    BlockTitle
} from 'framework7-react';

const MyPokemonPage = ({ myPokemonList, f7router }) => {
    const pokemonList = [];
    const [listOfCatchedPokemon, setListOfCatchedPokemon] = useState(myPokemonList);

    listOfCatchedPokemon.forEach(element => {
        pokemonList.push(
            <ListItem
                swipeout
                title={element.nickname}
                subtitle={element.species}
            >
                <SwipeoutActions right>
                    <SwipeoutButton delete confirmText="Are you sure you want to delete this item?">
                        Release
                    </SwipeoutButton>
                </SwipeoutActions>
            </ListItem>
        );
    });

    return(
        <Page 
            name="mypokemon"
        >
            <Navbar title="My Pokemon List">
                <Link slot="left" iconF7="arrowshape_turn_up_left_fill" onClick={() => {
                    f7router.navigate('/list/', {
                        props: {
                            myPokemonList: listOfCatchedPokemon
                        }
                    });
                }}></Link>
                <Link slot="right" iconF7="menu" panelOpen="#panel-menu"></Link>
            </Navbar>
            <BlockTitle>Swipe to left for release button</BlockTitle>
            <List>{pokemonList}</List>
        </Page>
    )
}

export default MyPokemonPage;
