import React, { useEffect, useState } from 'react';
import { 
    Page,
    Navbar,
    Link,
    List,
    ListItem,
    BlockTitle,
    f7
} from 'framework7-react';

const MyPokemonPage = ({ myPokemonList, f7router }) => {
    const pokemonList = [];
    const [listOfCatchedPokemon, setListOfCatchedPokemon] = useState(myPokemonList);
    const indexOfPokemon = listOfCatchedPokemon.map(element => element.nickname);

    listOfCatchedPokemon.forEach(element => {
        pokemonList.push(
            <ListItem link="#" title={element.nickname} subtitle={element.species} onClick={() => {
                f7.dialog.preloader(`Releasing ${element.nickname}...`);
                const releasePokemon = listOfCatchedPokemon;
                releasePokemon.splice(indexOfPokemon.indexOf(element.nickname), 1);
                setListOfCatchedPokemon(releasePokemon);
            }}>
                <img
                    slot="media"
                    src={element.src}
                    width="44"
                    alt={`Artwork of ${element.nickname}`}
                />
            </ListItem>
        );
    });

    useEffect(() => {
        f7.dialog.close();
        console.info('info: all catched pokemon', listOfCatchedPokemon);
    }, [listOfCatchedPokemon]);

    return(
        <Page name="my">
            <Navbar title="My Pokemon List">
                <Link slot="left" iconF7="house_alt_fill" onClick={() => {
                    f7router.navigate('/list/', {
                        props: {
                            myPokemonList: listOfCatchedPokemon
                        }
                    });
                }}></Link>
            </Navbar>
            <BlockTitle>Click for release</BlockTitle>
            <List>{pokemonList}</List>
        </Page>
    )
}

export default MyPokemonPage;
