import React, { useState, useEffect } from 'react';
import { 
    f7,
    Page,
    Navbar,
    List,
    ListItem,
    NavRight,
    Link,
    Searchbar,
    theme
} from 'framework7-react';

var pokeapiUrl = 'https://pokeapi.co/api/v2/pokemon?limit=20';
const WarningAlertTitle = '<i class="icon f7-icons color-orange" style="font-size: 35px;">exclamationmark</i>';

const ListPage = () => {
    const [consumePokeapi, setConsumePokeapi] = useState(false);
    const [pokemonList, setPokemonList] = useState([]);
    const [showPreloader, setShowPreloader] = useState(true);
    const listItem = [];

    const getPokemonDetail = (pokeapiResultsArray) => {
        pokeapiResultsArray.forEach(element => {
            fetch(element.url, {method: 'GET'})
            .then((response) => response.json())
            .then((response) => {
                if (response.sprites.length <= 0) {
                    f7.dialog.alert(
                        "Data from Pokeapi can't be processed",
                        WarningAlertTitle
                    );
                    setShowPreloader(false);
                    console.error('error: sprites from pokeapi is not an array\nsprites:', response.sprites);
                    return;
                }
                if (!Array.isArray(response.moves) || response.moves.length <= 0) {
                    f7.dialog.alert(
                        "Data from Pokeapi can't be processed",
                        WarningAlertTitle
                    );
                    setShowPreloader(false);
                    console.error('error: moves from pokeapi is not an array\nmoves:', response.moves);
                    return;
                }
                if (!Array.isArray(response.types) || response.types.length <= 0) {
                    f7.dialog.alert(
                        "Data from Pokeapi can't be processed",
                        WarningAlertTitle
                    );
                    setShowPreloader(false);
                    console.error('error: types from pokeapi is not an array\ntypes:', response.types);
                    return;
                }
                setPokemonList(state => [
                    ...state,
                    {
                        title: response.name,
                        subtitle: response.id,
                        slot: 'media',
                        src: response.sprites.other.dream_world.front_default,
                        width: '44',
                        alt: `artwork of ${response.name}`,
                    }
                ]);
            })
            .catch(error => {
                f7.dialog.alert(
                    'Pokeapi error',
                    WarningAlertTitle
                );
                setShowPreloader(false);
                console.error('error:', error);
                return;
            });
        });
    }

    useEffect(() => {
        fetch(pokeapiUrl, {method: 'GET'})
        .then((response) => response.json())
        .then((response) => {
            if (Array.isArray(response.results) && response.results.length > 0) {
                console.info('info: get results array from pokeapi');
                getPokemonDetail(response.results);
                pokeapiUrl = response.next;
                console.log(pokeapiUrl);
                return;
            }
            f7.dialog.alert(
                "Data from Pokeapi can't be processed",
                WarningAlertTitle
            );
            setShowPreloader(false);
            console.error('error: results from pokeapi is not an array');
        })
        .catch(error => {
            f7.dialog.alert(
                'Pokeapi error',
                WarningAlertTitle
            );
            setShowPreloader(false);
            console.error('error:', error);
        });
    }, [consumePokeapi]);

    pokemonList.forEach(element => {
        // console.info('render new pokemon:', element.title);
        listItem.push(
            <ListItem title={element.title.charAt(0).toUpperCase() + element.title.slice(1)} 
                subtitle={`#${element.subtitle}`}>
                <img
                    slot="media"
                    src={element.src}
                    width="44"
                    alt={`artwork of ${element.title}`}
                />
            </ListItem>
        );
    });

    const loadMore = () => {
        if (!consumePokeapi) {
            setConsumePokeapi(true);
            return;
        }
        setConsumePokeapi(false);
    }

    return (
        <Page 
            name="list"
            infinite
            infiniteDistance={50}
            infinitePreloader={showPreloader}
            onInfinite={loadMore}
        >
            <Navbar title="Pokemon List">
                <NavRight>
                    <Link
                        searchbarEnable=".searchbar-pokemon-list"
                        iconF7="search"
                    ></Link>
                </NavRight>
                <Searchbar
                    className="searchbar-pokemon-list"
                    expandable
                    searchContainer=".pokemon-list"
                    searchIn=".item-title, .item-subtitle"
                    disableButton={!theme.aurora}
                ></Searchbar>
            </Navbar>
            <List className="searchbar-not-found">
                <ListItem title="Pokemon not found"></ListItem>
            </List>
            <List className="pokemon-list searchbar-found" mediaList>{listItem}</List>
        </Page>
    )
}

export default ListPage;
