import React, { useState, useEffect } from 'react';
import { 
    f7,
    Page, 
    Navbar, 
    Icon,
    List,
    ListItem,
    theme,
    Subnavbar,
    Searchbar
} from 'framework7-react';

const WarningAlertTitle = '<i class="icon f7-icons color-orange" style="font-size: 35px;">exclamationmark</i>';

const ListPage = () => {
    const items = [];
    const [vlData, setVlData] = useState({ items: [] });
    const [pokeapiResults, setPokeapiResults] = useState([]);
    useEffect(() => {
        const requestOptions = {
            method: 'GET'
        };
        fetch('https://pokeapi.co/api/v2/pokemon?limit=2', requestOptions)
            .then((response) => response.json())
            .then((response) => {
                console.log(response);
                if (Array.isArray(response.results) && response.results.length > 0) {
                    console.log('pokeapi success');
                    // setPokeapiResults(response.results);
                    response.results.forEach(element => {
                        items.push(
                            {
                                title: element.name,
                                after: '$15',
                                subtitle: 'Beatles',
                                text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla sagittis tellus ut turpis condimentum, ut dignissim lacus tincidunt. Cras dolor metus, ultrices condimentum sodales sit amet, pharetra sodales eros. Phasellus vel felis tellus. Mauris rutrum ligula nec dapibus feugiat. In vel dui laoreet, commodo augue id, pulvinar lacus.',
                                slot: 'media',
                                src: 'https://cdn.framework7.io/placeholder/people-160x160-1.jpg',
                                width: '80',
                                alt: 'Yellow Submarine image'
                            },
                            {
                                title: element.name,
                                after: '$22',
                                subtitle: 'Queen',
                                text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla sagittis tellus ut turpis condimentum, ut dignissim lacus tincidunt. Cras dolor metus, ultrices condimentum sodales sit amet, pharetra sodales eros. Phasellus vel felis tellus. Mauris rutrum ligula nec dapibus feugiat. In vel dui laoreet, commodo augue id, pulvinar lacus.',
                                slot: 'media',
                                src: 'https://cdn.framework7.io/placeholder/people-160x160-2.jpg',
                                width: '80',
                                alt: "Don't Stop Me Now image"
                            },
                        );
                    });
                    f7.dialog.close();
                    return;
                }
                f7.dialog.close();
                f7.dialog.alert(
                    'Pokemon database error',
                    WarningAlertTitle
                );
                console.error('pokeapi error');
            });
    }, []);

    const searchAll = (query, searchItems) => {
        const found = [];
        for (let i = 0; i < searchItems.length; i += 1) {
            if (
                searchItems[i].title.toLowerCase().indexOf(query.toLowerCase()) >= 0 ||
                query.trim() === ''
            )
            found.push(i);
        }
        return found; // return array with mathced indexes
    };

    const renderExternal = (vl, newData) => {
        setVlData({ ...newData });
    };

    return (
        <Page name="list">
            <Navbar title="Pokemon List">
                <Icon slot="right" f7="poultry_leg"></Icon>
                <Subnavbar inner={false}>
                    <Searchbar
                        searchContainer=".virtual-list"
                        searchItem="li"
                        searchIn=".item-title"
                        disableButton={!theme.aurora}
                    />
                </Subnavbar>
            </Navbar>
            <List className="searchbar-not-found">
                <ListItem title="Nothing found" />
            </List>
            <List
                className="searchbar-found"
                medialList
                virtualList
                virtualListParams={{
                    items,
                    searchAll,
                    renderExternal,
                    height: theme.ios ? 63 : theme.md ? 73 : 77,
                }}
            >
                {vlData.items.map((item, index) => (
                    <ListItem
                        key={index}
                        mediaItem
                        link="#"
                        title={item.title}
                        after={item.after}
                        subtitle={item.subtitle}
                        text={item.text}
                        style={{ top: `${vlData.topPosition}px` }}
                        virtualListIndex={items.indexOf(item)}
                    >
                        <img
                            slot={item.slot}
                            src={item.src}
                            width={item.width}
                            alt={item.alt}
                        />
                    </ListItem>
                ))}
            </List>
        </Page>
    )
}

export default ListPage;
