import React, { useEffect, useState } from 'react';
import {
    Page,
    Navbar,
    Link,
    Panel,
    Block,
    BlockTitle,
    Chip,
    Card,
    CardHeader,
    CardContent,
    Fab,
    Icon,
    f7
} from 'framework7-react';
import './Cards.css';
import './Panel.css';

const SuccessAlertTitle = '<i class="icon f7-icons color-green" style="font-size: 35px;">checkmark_alt</i>';
const WarningAlertTitle = '<i class="icon f7-icons color-orange" style="font-size: 35px;">exclamationmark</i>';
const ErrorAlertTitle = '<i class="icon f7-icons color-red" style="font-size: 35px;">xmark</i>';

const DetailPage = ({ myPokemonList, title, src, moves, types, f7router }) => {
    const chipMoves = [];
    const chipTypes = [];
    const [listOfCatchedPokemon, setListOfCatchedPokemon] = useState(myPokemonList);
    const [temporaryListOfCatchedPokemon, setTemporaryListOfCatchedPokemon] = useState([]);
    const arrayPokemonSpeciesIndex = temporaryListOfCatchedPokemon.map(element => element.species);
    const arrayPokemonSpeciesLastIndex = arrayPokemonSpeciesIndex.lastIndexOf(title);
    const arrayPokemonSpeciesBeforeLastIndex = arrayPokemonSpeciesIndex.lastIndexOf(title) - 1;

    useEffect(() => {
        const getListOfCatchedPokemonByTitle = listOfCatchedPokemon.filter((element) => {
            return element.species === title;
        });
        setTemporaryListOfCatchedPokemon(getListOfCatchedPokemonByTitle);

        console.log('my pokemon', listOfCatchedPokemon);
        console.log('indexOf', getListOfCatchedPokemonByTitle);
        console.log(`${title} last index`, arrayPokemonSpeciesLastIndex);
        console.log(`${title} before last index`, arrayPokemonSpeciesBeforeLastIndex);
    }, [
        myPokemonList, 
        listOfCatchedPokemon, 
        arrayPokemonSpeciesLastIndex, 
        arrayPokemonSpeciesBeforeLastIndex, 
        title
    ]);

    moves.forEach(element => {
        chipMoves.push(
            <Chip text={element.move.name} />
        )
    });

    types.forEach(element => {
        chipTypes.push(
            <Chip text={element.type.name} />
        );
    });

    const catchPokemon = () => {
        f7.dialog.preloader('Catching Pokemon...');
        fetch('/pokemon/catch', {method: 'GET'})
        .then((response) => response.json())
        .then((response) => {
            if (!response.response) {
                f7.dialog.close();
                f7.dialog.alert(`Failed catching ${title}`, WarningAlertTitle);
                return;
            }
            f7.dialog.close();
            f7.dialog.alert(
                `${title} catched`,
                SuccessAlertTitle,
                () => {
                    if (arrayPokemonSpeciesLastIndex === -1 && arrayPokemonSpeciesBeforeLastIndex === -2) {
                        setListOfCatchedPokemon(state => [
                            ...state,
                            {
                                species: title,
                                nickname: `Mighty ${title}`,
                                src: src,
                                moves: moves,
                                types: types,
                            }
                        ]);
                        return;
                    }
                    if (arrayPokemonSpeciesLastIndex === 0 && arrayPokemonSpeciesBeforeLastIndex === -1) {
                        f7.dialog.preloader(`Add ${title} to your Pokemon list...`);
                        const requestOptions = {
                            method: 'POST',
                            headers: { 'Content-Type': 'application/json' },
                            body: JSON.stringify({
                                previousNickname: null,
                                currentNickname: listOfCatchedPokemon[arrayPokemonSpeciesLastIndex].nickname
                            })
                        };
                        fetch('/pokemon/getnickname', requestOptions)
                        .then((response) => response.json())
                        .then((response) => {
                            if (!response.response) {
                                f7.dialog.close();
                                f7.dialog.alert(`Failed to add ${title} into your Pokemon list`, WarningAlertTitle);
                                return;
                            }
                            f7.dialog.close();
                            f7.dialog.alert(`Successfully put ${title} into your Pokemon list`, SuccessAlertTitle);
                            setListOfCatchedPokemon(state => [
                                ...state,
                                {
                                    species: title,
                                    nickname: response.nickname,
                                    src: src,
                                    moves: moves,
                                    types: types,
                                }
                            ]);
                        })
                        .catch(error => {
                            f7.dialog.close();
                            f7.dialog.alert(
                                `Failed to get ${title} into your Pokemon list because add api error`, ErrorAlertTitle);
                            console.error('error:', error);
                        });
                        return;
                    }
                    f7.dialog.preloader(`Add ${title} to your Pokemon list...`);
                    const requestOptions = {
                        method: 'POST',
                        headers: { 'Content-Type': 'application/json' },
                        body: JSON.stringify({
                            previousNickname: listOfCatchedPokemon[arrayPokemonSpeciesBeforeLastIndex].nickname,
                            currentNickname: listOfCatchedPokemon[arrayPokemonSpeciesLastIndex].nickname
                        })
                    };
                    fetch('/pokemon/getnickname', requestOptions)
                    .then((response) => response.json())
                    .then((response) => {
                        if (!response.response) {
                            f7.dialog.close();
                            f7.dialog.alert(`Failed to add ${title} into your Pokemon list`, WarningAlertTitle);
                            return;
                        }
                        f7.dialog.close();
                        f7.dialog.alert(`Successfully put ${title} into your Pokemon list`, SuccessAlertTitle);
                        setListOfCatchedPokemon(state => [
                            ...state,
                            {
                                species: title,
                                nickname: response.nickname,
                                src: src,
                                moves: moves,
                                types: types,
                            }
                        ]);
                    })
                    .catch(error => {
                        f7.dialog.close();
                        f7.dialog.alert(
                            `Failed to get ${title} into your Pokemon list because add api error`, ErrorAlertTitle);
                        console.error('error:', error);
                    });
                }
            );
        })
        .catch(error => {
            f7.dialog.close();
            f7.dialog.alert(
                `Failed catching ${title} because catch api error`,
                ErrorAlertTitle
            );
            console.error('error:', error);
        });
    }

    return (
        <Page 
            name="list"
        >
            <Navbar title={`Detail of ${title}`}>
                <Link slot="left" iconF7="arrowshape_turn_up_left_fill" onClick={() => {
                    f7router.navigate('/list/', {
                        props: {
                            myPokemonList: listOfCatchedPokemon
                        }
                    });
                }}></Link>
                <Link slot="right" iconF7="menu" panelOpen="#panel-menu"></Link>
            </Navbar>
            <Panel right cover containerEl="#list" id="panel-menu">
                <Page>
                    <Block strong>
                        <Link panelClose>My Pokemon List</Link>
                    </Block>
                </Page>
            </Panel>
            <Fab position="center-bottom" slot="fixed" text="Catch" color="pink" onClick={() => {
                catchPokemon();
            }}>
                <Icon ios="f7:plus"></Icon>
            </Fab>
            <Card className="pokemon-card-header-pic">
                <CardHeader
                    className="no-border"
                    valign="bottom"
                    style={{
                        backgroundImage: `url(${src})`,
                    }}
                />
                <CardContent>
                    <BlockTitle>Moves</BlockTitle>
                    {chipMoves}
                    <BlockTitle>Types</BlockTitle>
                    {chipTypes}
                </CardContent>
            </Card>
        </Page>
    )
}

export default DetailPage;
