import React, { useEffect, useState } from 'react';
import {
    Page,
    Navbar,
    Link,
    Panel,
    Block,
    BlockTitle,
    Chip,
    Card,
    CardHeader,
    CardContent,
    Fab,
    Icon,
    f7
} from 'framework7-react';
import './Cards.css';
import './Panel.css';

var listOfCatchedPokemon = [];
const SuccessAlertTitle = '<i class="icon f7-icons color-green" style="font-size: 35px;">checkmark_alt</i>';
const WarningAlertTitle = '<i class="icon f7-icons color-orange" style="font-size: 35px;">exclamationmark</i>';
const ErrorAlertTitle = '<i class="icon f7-icons color-red" style="font-size: 35px;">xmark</i>';

const DetailPage = (props) => {
    const chipMoves = [];
    const chipTypes = [];
    const [addToMyPokemonList, setAddToMyPokemonList] = useState(false);
    // const [listOfCatchedPokemon, setListOfCatchedPokemon] = useState([]);

    props.moves.forEach(element => {
        chipMoves.push(
            <Chip text={element.move.name} />
        )
    });

    props.types.forEach(element => {
        chipTypes.push(
            <Chip text={element.type.name} />
        );
    });

    useEffect(() => {
        f7.dialog.preloader(`Add ${props.title} to your Pokemon list...`);
        let previousNickname = `Alif ${props.title}`;
        let currentNickname = `Alif ${props.title}-0`;
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ previousNickname: previousNickname, currentNickname: currentNickname})
        };

        fetch('/pokemon/getnickname', requestOptions)
        .then((response) => response.json())
        .then((response) => {
            if (!response.response) {
                f7.dialog.close();
                f7.dialog.alert(`Failed to get ${props.title} into your Pokemon list`, WarningAlertTitle);
                return;
            }
            f7.dialog.close();
            f7.dialog.alert(`Successfully put ${props.title} into your Pokemon list`, SuccessAlertTitle);
        })
        .catch(error => {
            f7.dialog.close();
            f7.dialog.alert(
                `Failed to get ${props.title} into your Pokemon list because add api error`, ErrorAlertTitle);
            console.error('error:', error);
        });

        // setListOfCatchedPokemon(state => [
        //     ...state,
        //     {
        //         nickname: nickname,
        //         title: props.title,
        //         src: props.src,
        //         moves: props.moves,
        //         types: props.types,
        //     }
        // ]);

        // listOfCatchedPokemon.forEach(element => {
        //     if (element.includes(props.title)) {
        //         console.log('Catched before');
        //         return;
        //     }
        //     listOfCatchedPokemon.push({
        //         nickname: nickname,
        //         title: props.title,
        //         src: props.src,
        //         moves: props.moves,
        //         types: props.types,
        //     });
        // });

        // localStorage.setItem(props.title, JSON.stringify({
        //     nickname: nickname,
        //     title: props.title,
        //     src: props.src,
        //     moves: props.moves,
        //     types: props.types,
        // }));
    }, [addToMyPokemonList, props.title]);

    const catchPokemon = () => {
        f7.dialog.preloader('Catching Pokemon...');
        fetch('/pokemon/catch', {method: 'GET'})
        .then((response) => response.json())
        .then((response) => {
            if (!response.response) {
                f7.dialog.close();
                f7.dialog.alert(`Failed catching ${props.title}`, WarningAlertTitle);
                return;
            }
            f7.dialog.close();
            f7.dialog.alert(
                `${props.title} catched`,
                SuccessAlertTitle,
                () => {
                    if (!addToMyPokemonList) {
                        setAddToMyPokemonList(true);
                        // console.log(listOfCatchedPokemon);
                        return;
                    }
                    setAddToMyPokemonList(false);
                    // console.log(listOfCatchedPokemon);
                }
            );
        })
        .catch(error => {
            f7.dialog.close();
            f7.dialog.alert(
                `Failed catching ${props.title} because catch api error`,
                ErrorAlertTitle
            );
            console.error('error:', error);
        });
    }

    return (
        <Page 
            name="list"
        >
            <Navbar title={`Detail of ${props.title}`}>
                <Link slot="left" iconF7="arrowshape_turn_up_left_fill" href="/list/"></Link>
                <Link slot="right" iconF7="menu" panelOpen="#panel-menu"></Link>
            </Navbar>
            <Panel right cover containerEl="#list" id="panel-menu">
                <Page>
                    <Block strong>
                        <Link panelClose>My Pokemon List</Link>
                    </Block>
                </Page>
            </Panel>
            <Fab position="center-bottom" slot="fixed" text="Catch" color="pink" onClick={() => {
                catchPokemon();
            }}>
                <Icon ios="f7:plus"></Icon>
            </Fab>
            <Card className="pokemon-card-header-pic">
                <CardHeader
                    className="no-border"
                    valign="bottom"
                    style={{
                        backgroundImage: `url(${props.src})`,
                    }}
                />
                <CardContent>
                    <BlockTitle>Moves</BlockTitle>
                    {chipMoves}
                    <BlockTitle>Types</BlockTitle>
                    {chipTypes}
                </CardContent>
            </Card>
        </Page>
    )
}

export default DetailPage;
