import React, { useState } from 'react';
import { 
    Page, 
    Navbar, 
    Icon,
    List,
    ListItem,
    theme
} from 'framework7-react';

const ListPage = () => {
    const items = [];
    items.push(
        {
            title: 'Yellow Submarine',
            after: '$15',
            subtitle: 'Beatles',
            text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla sagittis tellus ut turpis condimentum, ut dignissim lacus tincidunt. Cras dolor metus, ultrices condimentum sodales sit amet, pharetra sodales eros. Phasellus vel felis tellus. Mauris rutrum ligula nec dapibus feugiat. In vel dui laoreet, commodo augue id, pulvinar lacus.',
            slot: 'media',
            src: 'https://cdn.framework7.io/placeholder/people-160x160-1.jpg',
            width: '80',
        },
        {
            title: "Don't Stop Me Now",
            after: '$22',
            subtitle: 'Queen',
            text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla sagittis tellus ut turpis condimentum, ut dignissim lacus tincidunt. Cras dolor metus, ultrices condimentum sodales sit amet, pharetra sodales eros. Phasellus vel felis tellus. Mauris rutrum ligula nec dapibus feugiat. In vel dui laoreet, commodo augue id, pulvinar lacus.',
            slot: 'media',
            src: 'https://cdn.framework7.io/placeholder/people-160x160-2.jpg',
            width: '80',
        },
    );

    const [vlData, setVlData] = useState({ items: [] });

    const searchAll = (query, searchItems) => {
        const found = [];
        for (let i = 0; i < searchItems.length; i += 1) {
            if (
                searchItems[i].title.toLowerCase().indexOf(query.toLowerCase()) >= 0 ||
                query.trim() === ''
            )
            found.push(i);
        }
        return found; // return array with mathced indexes
    };

    const renderExternal = (vl, newData) => {
        setVlData({ ...newData });
    };

    return (
        <Page name="list">
            <Navbar title="Pokemon List">
                <Icon slot="right" f7="poultry_leg"></Icon>
            </Navbar>
            <List
                className="searchbar-found"
                medialList
                virtualList
                virtualListParams={{
                    items,
                    searchAll,
                    renderExternal,
                    height: theme.ios ? 63 : theme.md ? 73 : 77,
                }}
            >
                <ul>

                    {/* <ListItem
                        link="#"
                        title="Yellow Submarine"
                        after="$15"
                        subtitle="Beatles"
                        text="Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla sagittis tellus ut turpis condimentum, ut dignissim lacus tincidunt. Cras dolor metus, ultrices condimentum sodales sit amet, pharetra sodales eros. Phasellus vel felis tellus. Mauris rutrum ligula nec dapibus feugiat. In vel dui laoreet, commodo augue id, pulvinar lacus."
                    >
                        <img
                            slot="media"
                            src="https://cdn.framework7.io/placeholder/people-160x160-1.jpg"
                            width="80"
                        />
                    </ListItem> */}

                    {vlData.items.map((item, index) => (
                        <ListItem
                            key={index}
                            // mediaItem
                            link="#"
                            title={item.title}
                            after={item.after}
                            subtitle={item.subtitle}
                            // style={{ top: `${vlData.topPosition}px` }}
                            virtualListIndex={items.indexOf(item)}
                        >
                            <img
                                slot={item.slot}
                                src={item.src}
                                width={item.width}
                            />
                        </ListItem>
                    ))}

                </ul>
            </List>
        </Page>
    )
}

export default ListPage;
