import React, { useState, useEffect } from 'react';
import { 
    f7,
    Page, 
    Navbar, 
    Icon,
    List,
    ListItem,
    BlockTitle
} from 'framework7-react';

const WarningAlertTitle = '<i class="icon f7-icons color-orange" style="font-size: 35px;">exclamationmark</i>';

const ListPage = () => {
    const items = [];
    const [pokeapiResults, setPokeapiResults] = useState([]);

    useEffect(() => {
        const requestOptions = {
            method: 'GET'
        };
        fetch('https://pokeapi.co/api/v2/pokemon?limit=2', requestOptions)
            .then((response) => response.json())
            .then((response) => {
                console.log(response);
                if (Array.isArray(response.results) && response.results.length > 0) {
                    console.log('pokeapi success');
                    setPokeapiResults(response.results);
                    return;
                }
                f7.dialog.close();
                f7.dialog.alert(
                    'Pokemon database error',
                    WarningAlertTitle
                );
                console.error('pokeapi error');
            });
    }, []);
    console.log('pokeapi:', pokeapiResults);

    pokeapiResults.forEach(element => {
        items.push(
            <ListItem title={element.name} subtitle="Beatles">
                <img
                    slot="media"
                    src="https://cdn.framework7.io/placeholder/fashion-88x88-1.jpg"
                    width="44"
                />
            </ListItem>
        );
    });
    // items.push({
    //     title: element.name,
    //     after: '$15',
    //     subtitle: 'Beatles',
    //     text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla sagittis tellus ut turpis condimentum, ut dignissim lacus tincidunt. Cras dolor metus, ultrices condimentum sodales sit amet, pharetra sodales eros. Phasellus vel felis tellus. Mauris rutrum ligula nec dapibus feugiat. In vel dui laoreet, commodo augue id, pulvinar lacus.',
    //     slot: 'media',
    //     src: 'https://cdn.framework7.io/placeholder/people-160x160-1.jpg',
    //     width: '80',
    //     alt: 'Yellow Submarine image',
    // });

    return (
        <Page name="list">
            <Navbar title="Pokemon List">
                <Icon slot="right" f7="poultry_leg"></Icon>
            </Navbar>
            <BlockTitle>List</BlockTitle>
            <List mediaList>
                <ListItem title="Yellow Submarine" subtitle="Beatles">
                    <img
                        slot="media"
                        src="https://cdn.framework7.io/placeholder/fashion-88x88-1.jpg"
                        width="44"
                    />
                </ListItem>
                <ListItem link="#" title="Don't Stop Me Now" subtitle="Queen">
                    <img
                        slot="media"
                        src="https://cdn.framework7.io/placeholder/fashion-88x88-2.jpg"
                        width="44"
                    />
                </ListItem>
                <ListItem title="Billie Jean" subtitle="Michael Jackson">
                    <img
                        slot="media"
                        src="https://cdn.framework7.io/placeholder/fashion-88x88-3.jpg"
                        width="44"
                    />
                </ListItem>
            </List>
        </Page>
    )
}

export default ListPage;
