import React, { useEffect, useState } from 'react';
import { 
    Page,
    Navbar,
    Link,
    List,
    ListItem,
    BlockTitle,
    f7
} from 'framework7-react';

const SuccessAlertTitle = '<i class="icon f7-icons color-green" style="font-size: 35px;">checkmark_alt</i>';
const WarningAlertTitle = '<i class="icon f7-icons color-orange" style="font-size: 35px;">exclamationmark</i>';
const ErrorAlertTitle = '<i class="icon f7-icons color-red" style="font-size: 35px;">xmark</i>';

const MyPokemonPage = ({ myPokemonList, f7router }) => {
    const pokemonList = [];
    const [listOfCatchedPokemon, setListOfCatchedPokemon] = useState(myPokemonList);
    const indexOfPokemon = listOfCatchedPokemon.map(element => element.nickname);

    myPokemonList.forEach(element => {
        pokemonList.push(
            <ListItem link="#" title={element.nickname} subtitle={element.species} onClick={() => {
                f7.dialog.preloader(`Releasing ${element.nickname}...`);

                fetch('/pokemon/release', {method: 'GET'})
                .then((response) => response.json())
                .then((response) => {
                    setConsumePokeapi(false);
                    if (!response.response) {
                        console.info('info: release result', response.response);
                        f7.dialog.alert(
                            "Release failed",
                            WarningAlertTitle
                        );
                        return;
                    }
                    f7.dialog.alert(
                        "Release success",
                        SuccessAlertTitle
                    );
                    const releasePokemon = listOfCatchedPokemon;
                    releasePokemon.splice(indexOfPokemon.indexOf(element.nickname), 1);
                    setListOfCatchedPokemon(releasePokemon);
                    console.info('info: release result', response.response);
                })
                .catch(error => {
                    f7.dialog.alert(
                        'Pokeapi error',
                        ErrorAlertTitle
                    );
                    console.error('error:', error);
                });

            }}>
                <img
                    slot="media"
                    src={element.src}
                    width="44"
                    alt={`Artwork of ${element.nickname}`}
                />
            </ListItem>
        );
    });

    useEffect(() => {
        f7.dialog.close();
        console.info('info: all catched pokemon', listOfCatchedPokemon);
    }, [listOfCatchedPokemon]);

    return(
        <Page name="my">
            <Navbar title="My Pokemon List">
                <Link slot="left" iconF7="house_alt_fill" onClick={() => {
                    f7router.navigate('/list/', {
                        props: {
                            myPokemonList: listOfCatchedPokemon
                        }
                    });
                }}></Link>
            </Navbar>
            <BlockTitle>Click for release</BlockTitle>
            <List>{pokemonList}</List>
        </Page>
    )
}

export default MyPokemonPage;
