import React, { useState } from 'react';
import { Page, Gauge } from 'framework7-react';

const WelcomePage = ({f7router}) => {
    const [gaugeValue, setGaugeValue] = useState(0);

    const openLoginPage = () => {
        document.fonts.load('28px Framework7 Icons').then(() => {
            setGaugeValue(0.97);
            // f7router.navigate('/login/');
        })
    };

    return (
        <Page 
            name="welcome"
            onPageAfterIn={
                () => {
                    setGaugeValue(0.64);
                    openLoginPage();
                }
            }
            onPageBeforeOut={
                () => {
                    setGaugeValue(1);
                }
            }
        >
            <div class="page-content display-flex flex-direction-column justify-content-center">
                <div class="text-align-center">
                    <Gauge
                        type="circle"
                        value={gaugeValue}
                        size={250}
                        borderColor="#ff2d55"
                        borderWidth={10}
                        valueText={`${gaugeValue * 100}%`}
                        valueFontSize={41}
                        valueTextColor="#4cd964"
                        labelText="Loading assets"
                    />
                </div>
            </div>
        </Page>
    )
}

export default WelcomePage;
