import React, { useEffect, useState } from 'react';
import { 
    Page,
    Navbar,
    Link,
    List,
    ListItem,
    SwipeoutActions,
    SwipeoutButton,
    BlockTitle,
    f7
} from 'framework7-react';

const MyPokemonPage = ({ myPokemonList, f7router }) => {
    const pokemonList = [];
    const [listOfCatchedPokemon, setListOfCatchedPokemon] = useState(myPokemonList);
    const indexOfPokemon = listOfCatchedPokemon.map(element => element.nickname);

    const releasePokemon = (object, pokemonNickname) => {
        let releasePokemon = listOfCatchedPokemon;
        releasePokemon.splice(indexOfPokemon.indexOf(pokemonNickname), 1);
        setListOfCatchedPokemon(releasePokemon);
        f7.swipeout.delete(object);
    }

    listOfCatchedPokemon.forEach(element => {
        pokemonList.push(
            <ListItem
                swipeout
                title={element.nickname}
                subtitle={element.species}
            >
                <SwipeoutActions right>
                    <SwipeoutButton onClick={releasePokemon(this, element.nickname)}>
                        Release
                    </SwipeoutButton>
                </SwipeoutActions>
                <img
                    slot="media"
                    src={element.src}
                    width="44"
                    alt={`Artwork of ${element.species}`}
                />
            </ListItem>
        );
    });

    useEffect(() => {
        console.info('info: all catched pokemon', listOfCatchedPokemon);
    }, [listOfCatchedPokemon]);

    return(
        <Page name="my">
            <Navbar title="My Pokemon List">
                <Link slot="left" iconF7="house_alt_fill" onClick={() => {
                    f7router.navigate('/list/', {
                        props: {
                            myPokemonList: listOfCatchedPokemon
                        }
                    });
                }}></Link>
            </Navbar>
            <BlockTitle>Swipe to left for release button</BlockTitle>
            <List>{pokemonList}</List>
        </Page>
    )
}

export default MyPokemonPage;
