import React, { useState, useEffect } from 'react';
import { 
    f7,
    Page, 
    Navbar, 
    Icon,
    List,
    ListItem,
    BlockTitle
} from 'framework7-react';

const WarningAlertTitle = '<i class="icon f7-icons color-orange" style="font-size: 35px;">exclamationmark</i>';

const ListPage = () => {
    const items = [];
    const [pokeapiResults, setPokeapiResults] = useState([]);
    const [pokemonDetail, setPokemonDetail] = useState([]);

    useEffect(() => {
        fetch('https://pokeapi.co/api/v2/pokemon?limit=2', {method: 'GET'})
            .then((response) => response.json())
            .then((response) => {
                if (Array.isArray(response.results) && response.results.length > 0) {
                    console.info('info: get results array from pokeapi');
                    setPokeapiResults(response.results);
                    return;
                }
                f7.dialog.alert(
                    "Data from Pokeapi can't be processed",
                    WarningAlertTitle
                );
                console.error('error: results from pokeapi is not an array');
            })
            .catch(error => {
                f7.dialog.alert(
                    'Pokeapi error',
                    WarningAlertTitle
                );
                console.error('error:', error);
            });
    }, []);

    const getPokemonDetail = useEffect((url) => {
        fetch(url, 'GET')
            .then((response) => response.json())
            .then((response) => {
                console.log(response);
                if (!Array.isArray(response.sprites) || response.sprites.length <= 0) {
                    f7.dialog.alert(
                        "Data from Pokeapi can't be processed",
                        WarningAlertTitle
                    );
                    console.error('error: sprites from pokeapi is not an array\nsprites:', response.sprites);
                    return;
                }
                if (!Array.isArray(response.moves) || response.moves.length <= 0) {
                    f7.dialog.alert(
                        "Data from Pokeapi can't be processed",
                        WarningAlertTitle
                    );
                    console.error('error: moves from pokeapi is not an array\nmoves:', response.moves);
                    return;
                }
                if (!Array.isArray(response.types) || response.types.length <= 0) {
                    f7.dialog.alert(
                        "Data from Pokeapi can't be processed",
                        WarningAlertTitle
                    );
                    console.error('error: types from pokeapi is not an array\mtypes:', response.types);
                    return;
                }
                setPokemonDetail(response);
                console.info('info: consume pokeapi success');
            });
    }, []);

    pokeapiResults.forEach(element => {
        // items.push(
        //     <ListItem title={element.name} subtitle="Beatles">
        //         <img
        //             slot="media"
        //             src="https://cdn.framework7.io/placeholder/fashion-88x88-1.jpg"
        //             width="44"
        //         />
        //     </ListItem>
        // );

        getPokemonDetail(element.url);
    });
    console.log(pokemonDetail);

    return (
        <Page name="list">
            <Navbar title="Pokemon List">
                <Icon slot="right" f7="poultry_leg"></Icon>
            </Navbar>
            <BlockTitle>List</BlockTitle>
            <List mediaList>{items}</List>
        </Page>
    )
}

export default ListPage;
